function Projects_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Projects');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Projects_name' => Request::val('Projects_name', ''),
		'Description' => Request::val('Description', ''),
		'Category' => Request::lookup('Category', ''),
		'ProjectTags' => Request::multipleChoice('ProjectTags', ''),
		'Image_1' => Request::fileUpload('Image_1', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('Projects', 'Image_1', 'tv'));
				createThumbnail($name, getThumbnailSpecs('Projects', 'Image_1', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('Projects', 'Image_1', Request::val('SelectedID'));
			},
		]),
		'Image_Uploaded' => Request::fileUpload('Image_Uploaded', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('Projects', 'Image_Uploaded', Request::val('SelectedID'));
			},
		]),
		'Geo_place' => Request::val('Geo_place', ''),
	];


	// hook: Projects_before_insert
	if(function_exists('Projects_before_insert')) {
		$args = [];
		if(!Projects_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Projects', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Projects', $recID, calculated_fields()['Projects']);

	// hook: Projects_after_insert
	if(function_exists('Projects_after_insert')) {
		$res = sql("SELECT * FROM `Projects` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Projects_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Projects', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Projects_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

