	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Projects_name').replaceWith('<div class=\"form-control-static\" id=\"Projects_name\">' + (jQuery('#Projects_name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Category').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Category_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#ProjectTags').replaceWith('<div class=\"form-control-static\" id=\"ProjectTags\">' + (jQuery('#ProjectTags').val() || '') + '</div>'); jQuery('#ProjectTags-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#s2id_ProjectTags').remove();\n";
		$jsReadOnly .= "\tjQuery('#Image_1').replaceWith('<div class=\"form-control-static\" id=\"Image_1\">' + (jQuery('#Image_1').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Image_Uploaded').replaceWith('<div class=\"form-control-static\" id=\"Image_Uploaded\">' + (jQuery('#Image_Uploaded').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Image_Uploaded, #Image_Uploaded-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#Geo_place').replaceWith('<div class=\"form-control-static\" id=\"Geo_place\">' + (jQuery('#Geo_place').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Geo_place, #Geo_place-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(Category)%%>', $combo_Category->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Category)%%>', $combo_Category->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Category)%%>', urlencode($combo_Category->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(ProjectTags)%%>', $combo_ProjectTags->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(ProjectTags)%%>', $combo_ProjectTags->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['Category' => ['Categories', 'Category'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Projects_name)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Category)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ProjectTags)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Image_1)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="Image_1" id="Image_1" data-filetypes="jpg|jpeg|gif|png|webp" data-maxsize="10240000" accept=".jpg,.jpeg,.gif,.png,.webp">'), $templateCode);
	if($AllowUpdate && $row['Image_1'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(Image_1)%%>', '<br><input type="checkbox" name="Image_1_remove" id="Image_1_remove" value="1"> <label for="Image_1_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(Image_1)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(Image_Uploaded)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="Image_Uploaded" id="Image_Uploaded" data-filetypes="jpg|jpeg|gif|png|webp" data-maxsize="10240000" accept=".jpg,.jpeg,.gif,.png,.webp">'), $templateCode);
	if($AllowUpdate && $row['Image_Uploaded'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(Image_Uploaded)%%>', '<br><input type="checkbox" name="Image_Uploaded_remove" id="Image_Uploaded_remove" value="1"> <label for="Image_Uploaded_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(Image_Uploaded)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(Geo_place)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Projects_name)%%>', safe_html($urow['Projects_name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Projects_name)%%>', html_attr($row['Projects_name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Projects_name)%%>', urlencode($urow['Projects_name']), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<textarea name="Description" id="Description" rows="5">' . safe_html(htmlspecialchars_decode($row['Description'])) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<div id="Description" class="form-control-static">' . $row['Description'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(Description)%%>', nl2br($row['Description']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Description)%%>', urlencode($urow['Description']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Category)%%>', safe_html($urow['Category']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Category)%%>', html_attr($row['Category']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Category)%%>', urlencode($urow['Category']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(ProjectTags)%%>', safe_html($urow['ProjectTags']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(ProjectTags)%%>', html_attr($row['ProjectTags']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ProjectTags)%%>', urlencode($urow['ProjectTags']), $templateCode);
		$row['Image_1'] = ($row['Image_1'] != '' ? $row['Image_1'] : 'blank.gif');
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Image_1)%%>', safe_html($urow['Image_1']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Image_1)%%>', html_attr($row['Image_1']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Image_1)%%>', urlencode($urow['Image_1']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Image_Uploaded)%%>', safe_html($urow['Image_Uploaded']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Image_Uploaded)%%>', html_attr($row['Image_Uploaded']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Image_Uploaded)%%>', urlencode($urow['Image_Uploaded']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Geo_place)%%>', safe_html($urow['Geo_place']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Geo_place)%%>', html_attr($row['Geo_place']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Geo_place)%%>', urlencode($urow['Geo_place']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Projects_name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Projects_name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<textarea name="Description" id="Description" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(Category)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Category)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(ProjectTags)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ProjectTags)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Image_1)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(Image_Uploaded)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Image_Uploaded)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Geo_place)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Geo_place)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('Image_UploadedEdit')) { document.getElementById('Image_UploadedEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('Image_UploadedEditLink')) { document.getElementById('Image_UploadedEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Projects');
	if($selected_id) {
		$jdata = get_joined_record('Projects', $selected_id);
		if($jdata === false) $jdata = get_defaults('Projects');
		$rdata = $row;
	}
	$templateCode .= loadView('Projects-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Projects_dv
	if(function_exists('Projects_dv')) {
		$args = [];
		Projects_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}